import React from 'react';
import './AudioPlayer.css'

class AudioPlayer extends React.Component {
  constructor(props) {
    super(props);
    this.audio = new Audio(this.props.previewURL);
    this.play = this.play.bind(this);
    this.pause = this.pause.bind(this);
  }

  play() {
    stopAll();
    this.audio.play();
  }

  stopAll() {
      var audioButtons = document.getElementsByClassName('audio');
          let i = media.length;
      while (i--) {
          audioButtons[i].pause();
      }
  }

  pause() {
    this.audio.pause();
  }

  render() {
    return (
      <div>
        <button className="actionButton audio" onClick={this.play}>&#9658;</button>
        <button className="actionButton stop" onClick={this.pause}>&#10073;&#10073;</button>
      </div>
    );
  }
}

export default AudioPlayer;
