import React from 'react';
import './Track.css';
import AudioPlayer from '../AudioPlayer/AudioPlayer';

class Track extends React.Component {
  constructor(props) {
    super(props);
    this.addTrack = this.addTrack.bind(this);
    this.removeTrack = this.removeTrack.bind(this);
  }

  renderAction() {
    const action = this.props.isRemoval ? '-' : '+';
    const actionFunction = this.props.isRemoval ? this.removeTrack : this.addTrack;
    return <a className="Track-action" onClick={actionFunction}>{action}</a>;
  }

  addTrack() {
    this.props.onAdd(this.props.track);
  }

  removeTrack() {
    this.props.onRemove(this.props.track);
  }

  render() {
    return (
      <div className="Track">
        <div className="Track-information">
          <h3>{this.props.track.name}</h3>
          <p>{this.props.track.artist} | {this.props.track.album}</p>
        </div>
        <div>
        {this.renderAction()}
        </div>

        {this.props.track.preview_url ? <div><AudioPlayer previewURL={this.props.track.preview_url}/></div> : ''}

      </div>
    );
  }
};

export default Track;
